import { createSlice, nanoid } from "@reduxjs/toolkit";

const initialState = [];

const dexSlice = createSlice({
  name: "dex",
  initialState,
  reducers: {
    pokeAdded(state, action) {
      state.push(action.payload);
    },
  },
});

export const { pokeAdded } = dexSlice.actions;

export default dexSlice.reducer;
