import "./App.css";
import { React, useState, useEffect } from "react";
const Pokedex = require("pokedex-promise-v2");
const P = new Pokedex();
const fetchUrl = "/api/v2/pokemon/";
const urlArray = [];

function App() {
  const [pokeIndex, setPokeIndex] = useState(1);
  const [pokemonsNumber, setPokemonsNumber] = useState(20);
  const [pokemons, setPokemons] = useState();

  useEffect(() => {
    for (let i = 1; i <= pokemonsNumber; i++) {
      let str = fetchUrl + i;
      urlArray.push(str);
    }
    P.resource(urlArray).then(function (response) {
      setPokemons(response); // resource function accepts singles or arrays of URLs/paths
    });
  }, [pokemonsNumber]);

  pokemons && console.log(pokemons);

  return (
    <div className="App">
      <button>load more</button>
    </div>
  );
}

export default App;
