import styled from "styled-components";

export const StyledNavbar = styled.nav`
  padding: 10px;
  display: flex;
  justify-content: space-between;
  border-bottom: 1px solid #f2f2f2;
  font-family: "Quicksand";
  background-color: white;

  h1 {
    color: #d8a31a;
    font-size: 28px;
    margin-left: 200px;
  }

  .links {
    margin-top: 30px;
    margin-right: 400px;
  }

  a {
    margin-left: 16px;
    text-decoration: none;
    padding: 6px;
  }

  a:hover {
    color: #d8a31a;
    transition: all 0.3s;
  }
`;
