import styled from "styled-components";

export const StyledNavbar = styled.nav`
  padding: 20px;
  display: flex;
  align-items: center;
  max-width: 600px;
  margin: 0 auto;
  border-bottom: 1px solid #f2f2f2;
  font-family: "Quicksand";

  h1 {
    color: #1663b1;
    font-size: 30px;
  }

  .links {
    margin-left: auto;
  }

  a {
    margin-left: 16px;
    text-decoration: none;
    padding: 6px;
  }

  a:hover {
    color: #f1356d;
  }
`;
