import styled from "styled-components";

export const StyledPokeDetailCard = styled.div`
  font-family: "Quicksand";

  width: 750px;
  margin: 10px auto;
  padding-top: 5px;
  color: #877455;
  background-color: #ffeecb;

  span {
    font-size: 14px;
  }

  h2 {
    margin-left: 40%;
  }

  h4 {
    margin-bottom: 5px;
  }

  span {
    color: #d8a31a;
  }

  img {
    width: 60%;
    margin-left: 10px;
    margin-top: -40px;
  }

  .info {
    display: flex;
    justify-content: space-between;
  }

  .second-col {
    margin-left: 50px;
    padding-right: 30px;
  }
`;
