import styled from "styled-components";

export const StyledNavbar = styled.nav`
  nav {
    padding: 20px;
    display: flex;
    align-items: center;
    max-width: 600px;
    margin: 0 auto;
    border-bottom: 1px solid #f2f2f2;
  }
  h1 {
    font-family: "Quicksand";
    color: #f1356d;

    @media screen and (max-width: 768px) {
      font-size: 22px;
    }
  }
`;
