import styled from "styled-components";

export const StyledPokeType = styled.div`
  background-color: ${(props) => props.theme[`${props.type}`]};
  color: white;
  width: 75px;
  height: 30px;
  p {
    font-weight: 700;
    margin-left: 10px;
    margin-top: -9px;
  }
`;
