import styled from "styled-components";

export const StyledLoadMoreBtn = styled.button`
  border: none;
  background-color: ${({ theme }) => theme.text};
  color: ${({ theme }) => theme.body};
  width: 300px;
  height: 30px;
  border-radius: 10px;
  font-weight: 700;
`;
