import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import PokeDetailCard from "../components/PokeDetailCard";
const Pokedex = require("pokedex-promise-v2");

const PokemonDetails = () => {
  const P = new Pokedex();
  let params = useParams();
  let id = params.id;
  const [pokemon, setPokemon] = useState(null);
  const [evolution, setEvolution] = useState(null);

  let urlArray = [`/api/v2/pokemon/${id}`, `/api/v2/evolution-chain/${id}`];

  useEffect(() => {
    P.resource(urlArray)
      .then((response) => {
        setPokemon(response[0]);
        setEvolution(response[1]);
      })
      .catch((err) => console.log(err));
  }, []);

  console.log(id);
  return (
    <div style={{ minHeight: "80vh" }}>
      <PokeDetailCard pokemon={pokemon} evolution={evolution} />
    </div>
  );
};

export default PokemonDetails;
