import styled from "styled-components";

export const StyledPokeDetailCard = styled.div`
  font-family: "Quicksand";
  border: 1px solid goldenrod;
  width: 700px;
  margin: 10px auto;
  padding-top: 5px;
  color: #877455;

  span {
    font-size: 16px;
  }

  h2 {
    margin-left: 40%;
  }

  span {
    color: #d8a31a;
  }

  img {
    width: 70%;
    margin-left: -10px;
  }

  .info {
    display: flex;
    justify-content: space-between;
  }

  .second-col {
    padding-right: 30px;
  }
`;
