import styled from "styled-components";

export const StyledPokeDetailCard = styled.div`
  text-align: center;
  font-family: "Quicksand";
  border: 1px solid goldenrod;
  width: 600px;
  margin: 10px auto;
  padding-top: 5px;
  color: #877455;

  img {
    width: 60%;
  }

  .info {
    display: flex;
    justify-content: space-between;
  }

  .second-col {
    padding-right: 30px;
  }
`;
