import styled from "styled-components";

export const StyledSearchBar = styled.div`
  width: 100%;
  height: 105px;
  padding: 25px 20px 0px 20px;
  box-sizing: border-box;
  color: #fff;
`;

export const StyledSearchBarContent = styled.div`
  max-width: 1280px;
  width: 100%;
  height: 55px;
  background: #e2e2e2;
  margin: 0 auto;
  border-radius: 40px;
  position: relative;
  color: #d8a31a;

  input {
    font-family: "Abel", sans-serif;
    font-size: 26px;
    position: absolute;

    left: 50px;
    margin: 8px 0;
    padding: 0 0 0 60px;
    border: none;
    width: 95%;
    background: transparent;
    height: 40px;
    box-sizing: border-box;

    :focus {
      outline: none;
      color: #d8a31a;
    }

    :focus::placeholder {
      color: transparent;
    }

    ::placeholder {
      color: #d8a31a;
    }

    @media screen and (max-width: 720px) {
      font-size: 28px;
    }
  }
`;
