import styled from "styled-components";

export const StyledPokeDetailCard = styled.div`
  font-family: "Quicksand";

  max-width: 900px;
  margin: 10px auto;
  padding-top: 5px;
  color: #877455;
  background-color: #ffeecb;
  position: relative;

  span {
    font-size: 14px;
  }

  h2 {
    margin-left: 40%;
  }

  h4 {
    margin-bottom: 5px;
  }

  span {
    color: #d8a31a;
  }

  img {
    width: 40%;
    margin-left: 10%;
    margin-top: -40px;
    float: left;
  }

  .info {
    display: flex;
    justify-content: space-between;
  }

  .first-col {
    width: 100%;
  }
  .second-col {
    margin-left: 0;
    padding-right: 30px;
    border-left: 1px solid white;
    padding-left: 15px;
    padding-bottom: 15px;
  }

  .arrow-container {
    display: flex;
    justify-content: space-between;

    width: 100%;
  }

  .arrow-container a {
    text-decoration: none;
    color: #d8a31a;
  }

  .arrow-container a:hover {
    text-decoration: none;
    color: #877455;
    transition: all 0.3s;
  }
`;
