import styled from "styled-components";

export const StyledPokeDex = styled.div`
  max-width: 500px;
  margin: 15px auto;
  padding: 10px 20px;
  min-height: 82vh;
  background-color: #e6f4f1;
  font-family: "Quicksand";
  color: #d8a31a;
  text-align: center;

  div {
    height: 200px;
    width: 100%;
    margin-left: 0;
    margin-bottom: 10px;
    position: relative;
    border-bottom: 1px solid #d8a31a;
  }

  img {
    width: 30%;
  }

  h4 {
    position: absolute;
    top: 0;
    left: 50%;
  }
`;
