import styled from "styled-components";
import Image from "../images/ash.jpg";
export const StyledHero = styled.div`
  font-family: "Quicksand";
  h1 {
    color: #e2e2e2;
  }

  h4 {
    padding-top: 13px;
  }

  a {
    text-decoration: none;
  }

  .hero-button {
    color: #e6f4f1;
    background-color: #d8a31a;
    border-radius: 15px;
    width: 300px;
    height: 50px;
    text-align: center;
    margin-left: 110px;
  }

  .hero-button:hover {
    opacity: 0.7;
  }

  .hero-container {
    width: 100%;
    height: 80vh;
    background-image: url(${Image});
    background-repeat: no-repeat;
    position: relative;
  }

  .hero-content {
    position: absolute;
    left: 15%;
    top: 40%;
    --ms-transform: translateY(-50%);
    transform: translateY(-50%);
  }
`;
